FROM ONGOING OG, MEM_JOIN MJ
WHERE OG.MEM_JOIN_NUM = MJ.MEM_JOIN_NUM
  AND MJ.ROOM_NUM = #{room_num}
  AND MJ.ID_NUM = #{my_id}

