mapper.select:
SELECT FEEDNUM, IDNUM, ID, NAME, PHOTO, NICKNAME, ROOMNUM
FROM 
(
    SELECT FEEDNUM, IDNUM, ID, NAME, PHOTO, NICKNAME, ROOMNUM
    FROM FEED_VIEW
    WHERE ID = #{id}
    AND ROOMNUM = #{roomNum}
    ORDER BY FEEDNUM DESC
)
WHERE ROWNUM = 1

mapper.update:
UPDATE FEED
SET FEED_DEL_DATE = SYSDATE
WHERE FEED_NUM = #{feedNum}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM ONGOING OG JOIN MEM_JOIN MJ
ON OG.MEM_JOIN_NUM = MJ.MEM_JOIN_NUM
WHERE MJ.ROOM_NUM = #{roomNum}
  AND ID_NUM = #{idNum}
